import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./login.css";
import swal from "sweetalert";

function Register()
{
    const[email, setEmail] = useState("");
    const[firstName, setFirstName] = useState("");
    const[lastName, setLastName] = useState("");
    const[phone, setPhone] = useState("");

    function registration(e2)
    {
        e2.preventDefault();
        swal("Account Created");
    }

    return (
        <div>
            <div className="row justify-content-center">
                <div className="col-md-4 card">
                    <h1 className="header">User Registration</h1>

                    <form onSubmit={registration}>
                        <input className="form-control" type="text" placeholder="email" 
                            onChange={(e)=> setEmail(e.target.value)}/>                            
                        <input className="form-control" type="text" placeholder="firstName" 
                            onChange={(e)=> setFirstName(e.target.value)}/>
                        <input className="form-control" type="text" placeholder="lastName" 
                            onChange={(e)=> setLastName(e.target.value)}/>
                        <input className="form-control" type="text" placeholder="phone" 
                            onChange={(e)=> setPhone(e.target.value)}/>
                        <input className="btn btn-primary" type="submit" value="Register" />
                    </form>

                </div>
            </div>
        </div>
    )
}

export default Register;
