import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./login.css";
import swal from "sweetalert";

function Login()
{
    const[username, setUsername] = useState("");
    const[password, setPassword] = useState("");

    function validate(event)
    {
        event.preventDefault();
        if(username === "javascript" && password === "react")
        {
            swal('Login successful', 'You remembered your details you dumb whore', 'success');
        }

        else
        {
            swal("Invalid Credentials", "Don't let your age get to you", "error");
        }
    }

    return (
        <div>
            <div className="row justify-content-center">
                <div className="col-md-4 card">
                    <h1 className="header">User Authentication</h1>

                    <form onSubmit={validate}>
                        <input
                            className="form-control"
                            type="text"
                            placeholder="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                        <input
                            className="form-control"
                            type="text"
                            placeholder="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <input className="btn btn-primary" type="submit" value="Login" />
                    </form>

                </div>
            </div>
        </div>
    )
}

export default Login;
