import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./login.css";
import swal from "sweetalert";

function Register()
{
    const[email, setEmail] = useState("");
    const[firstName, setFirstName] = useState("");
    const[lastName, setLastName] = useState("");
    const[phone, setPhone] = useState("");

    /* function validate(event)
    {
        event.preventDefault();
        if(firstName === "javascript" && lastName === "react")
        {
            // alert("Login successful");
            swal('Login successful', 'You remembered your details you dumb whore', 'success');
        }

        else
        {
            // alert("Invalid Credentials")
            swal("Invalid Credentials", "Don't let your age get to you", "error");
        }
    } */

    return (
        <div>
            <div className="row justify-content-center">
                <div className="col-md-4 card">
                    <h1 className="header">User Registration</h1>

                    <form onSubmit={validate}>
                        <input className="form-control" type="text" placeholder="email" 
                            onChange={(e)=> setEmail(e.target.value)}/>                            
                        <input className="form-control" type="text" placeholder="firstName" 
                            onChange={(e)=> setFirstName(e.target.value)}/>
                        <input className="form-control" type="text" placeholder="lastName" 
                            onChange={(e)=> setLastName(e.target.value)}/>
                        <input className="form-control" type="text" placeholder="phone" 
                            onChange={(e)=> setPhone(e.target.value)}/>
                        <input className="btn btn-primary" type="submit" value="Register" />
                    </form>

                </div>
            </div>
        </div>
    )
}

export default Register;
